def create_prime_list(upper_limit):
    prime_list = []
    for num in range(2,upper_limit+1):
        flag=0
        for i in range(2,num):
            if num % i == 0:
                flag=1
        if flag==0:
            prime_list.append(num)
    return prime_list

def is_palindrome(sample):
    res = True
    i = 0
    j = len(sample) - 1
    while i < j:
        if sample[i] != sample[j]:
            res = False
            break
        else:
            i += 1
            j -= 1

    return res

