    else if ($n == 1)
        return '1';
    else
    {
        $fibonaccinumbers[0] = 0;
        $fibonaccinumbers[1] = 1;
        for ($i = 2; $i <= $n; $i++)
        {
            $fibonaccinumbers[$i] = $fibonaccinumbers[$i - 1] +
