int perfectNo(int low, int high) 
{ 
    int sum = 0; 
    for (int i=low; i<high; i++) 
    { 
        int div_sum = 0; 

        for (int j=1; j<i; j++) 
            if (i % j == 0) 
                div_sum += j; 

        if (div_sum == i && i != 1) 
            sum += i; 
    } 
    return sum; 
} 

