string removeAdjacentDuplicates(string str) 
{ 
    int l = str.Length; 
    char[] array = str.ToCharArray(); 

    int index = 0; 
    for (int i = 0; i < l; i++) 
    { 
        if (i == 0 || array[i - 1] != array[i]) 
            array[index++] = array[i]; 
        else
            while (i + 1 < l && array[i + 1] == array[i]) 
                i++; 
    } 

    return new string(array, 0, index); 
}

