char encrypt(char ch, int key) 
{ 
    if (ch >= 'a' && ch <= 'z') 
        return (char)('a' + (ch - 'a' + key) % 26); 
    else if (ch >= 'A' && ch <= 'Z') 
        return (char)('A' + (ch - 'A' + key) % 26); 
    else
        return ch; 
}

