function isPrime(n)
{
    // alleven numbers are not prime
    if (n % 2 == 0) return false;

    // check odd divisors b/w 3 and sqrt(n)
    let limit = Math.sqrt(n);
    for (let i = 3; i <= limit; i=i+2)
    {
        // if evenly divisible then not prime
        if (n % i == 0) return false;
    }

    return true;
}

