def minMoves(arr): 
    n = len(arr) 
    moves = 0
    for i in range(n):  
        for j in range(n - 1):
            if arr[j] > arr[j + 1]:  
                temp = arr[j]
                arr[j] = arr[j + 1] 
                arr[j + 1] = temp
                moves += 1

    return moves

