using namespace std; 

class Matrix { 
    private: 
        vector<vector<int>> matrix; 
        int rows, cols; 

    public: 
        // Default constructor 
        Matrix(int r = 0, int c = 0); 

        // Parametrized constructor
        Matrix(int r, int c, int ver[][c]);  

        // Copy constructor 
        Matrix(Matrix& orig); 

        // Destructor 
        ~Matrix(); 

        // Access matrix elements 
        int operator()(const int& row, 
                       const int& col) const; 
        int& operator()(const int& row, 
                        const int& col); 

        // Utility functions 
        int getRows() const; 
        int getCols() const; 
}; 

Matrix::Matrix(int r, int c) 
    : rows(r), cols(c) 
{ 
    // Allocate memory for the matrix 
    matrix.resize(rows); 
    for (int i = 0; i < rows; i++) 
        matrix[i].resize(cols); 
} 

Matrix::Matrix(int r, int c, int ver[][c])
    : rows(r), cols(c)
{
    // Allocate memory for the matrix
    matrix.resize(rows);
    for (int i = 0; i < rows; i++)
        matrix[i].resize(cols);

    // Copy the matrix
    for (int i = 0; i < rows; i++) 
        for (int j = 0; j < cols; j++) 
            matrix[i][j] = ver[i][j]; 
} 


Matrix::Matrix(Matrix& orig) 
    : Matrix(orig.getRows(), orig.getCols()) 
{ 
    for (int i = 0; i < rows; i++) 
        for (int j = 0; j < cols; j++) 
            matrix[i][j] = orig(i, j); 
} 

Matrix::~Matrix()
{
    rows = 0;
    cols = 0;
    matrix.clear();
}

int Matrix::getRows() const
{ 
    return rows; 
} 

int Matrix::getCols() const
{ 
    return cols; 
} 

int Matrix::operator()(const int& row, 
                       const int& col) const
{ 
    return matrix[row][col]; 
} 

int& Matrix::operator()(const int& row, 
                        const int& col)
{ 
    return matrix[row][col]; 
}

