//Function to find the sum of digits 
int SumOfDigits(int n) 
{ 
	int sum = 0; 
	while(n>0) 
	{ 
		//Extracting the last digit of the number 
		int rem = n % 10;  
		sum = sum + rem; 

		//Removing the last digit from the number 
		n = n / 10; 
	} 
	return sum; 
} 

