int findSecondMax(int arr[], int n) 
{ 
    // Initialize maximum and second maximum
    int max = INT_MIN; 
    int smax = INT_MIN; 

    for (int i = 0; i < n; i++) 
    { 
        // If current element is greater than max 
        if (arr[i] > max) 
        { 
            // update second maximum 
            smax = max; 

            // update maximum element 
            max = arr[i]; 
        } 

        // If arr[i] is in between max and second maximum 
        else if (arr[i] > smax && arr[i] != max) 
            smax = arr[i]; 
    } 
    // return second maximum 
    return smax; 
} 

