{
  int origNum = num;
  int sum = 0;
  int digits = 0;

  while (num != 0)
  {
    digits++;
    num /= 10;
  }
  num = origNum;
  while (num != 0)
  {
    sum +=Math.Pow(num % 10, digits);
    num /= 10;
  }

  if (sum== origNum)
     return true;

  return false;
}

