int add(Stack *st) {
    int x = pop(st);
    int y = pop(st);
    return x + y;
}

int subtract(Stack *st) {
    int x = pop(st);
    int y = pop(st);
    return x - y;
}

int multiply(Stack *st) {
    int x = pop(st);
    int y = pop(st);
    return x * y;
}

int divide(Stack *st) {
    int x = pop(st);
    int y = pop(st);
    return x / y;
}

def binary_search(nums, target):
  low = 0
  high = len(nums) - 1

  while low <= high:
    mid = (low + high) // 2
    if target == nums[mid]:
      return mid
    elif target < nums[mid]:
      high = mid - 1
    else:
      low = mid + 1

  return -1

def get_smallest_largest(arr):
    """
    Function to return the smallest and largest numbers in a list
    Parameters:
        arr: An unsorted list of numbers
    Returns:
        A tuple containing the smallest and largest numbers in the list
    """
    smallest = arr[0]
    largest = arr[0]

    for elem in arr:
        if elem < smallest:
            smallest = elem
        if elem > largest:
            largest = elem

    return (smallest, largest)

