  public static string DecimalToBinary(int n)
  {
    string binary = "";
    int remainder;
    //without using bitwise operators
    while (n != 0)
    {
      remainder = n % 2;
      n /= 2;
      binary = remainder + binary; 
    }
    return binary;
  }

