fn to_title_case(s: String) -> String {
    let words: Vec<&str> = s.split_whitespace().collect();
    let mut title_cased = String::new();
    for word in words {
        let mut chars = word.chars();
        title_cased.push_str(&chars.next().unwrap().to_uppercase().to_string());
        for char in chars {
            title_cased.push_str(&char.to_lowercase().to_string());
        }
        title_cased.push(' ');
    }
    title_cased.trim().to_string()

}

def sieve_of_eratosthenes(lower, upper):
    # Create a list of prime numbers
    prime_list = [True] * (upper + 1)
    prime_list[0] = False
    prime_list[1] = False

    # Iterate over the list until the square root of the upper limit
    for i in range(2, int(upper ** 0.5) + 1):
        # If i is true, then it is prime
        if prime_list[i]:
            # Set all multiples of i to false
            for j in range(i*i, upper + 1, i):
                prime_list[j] = False

    # Get the list of prime numbers between lower and upper
    prime_nums = []
    for i in range(lower, upper + 1):
        if prime_list[i]:
            prime_nums.append(i)

    return prime_nums

if __name__ == "__main__":
    lower = 0
    upper = 50
    print(sieve_of_eratosthenes(lower, upper))

