def is_prime(n):
    if n <= 1:
        return False
    if n == 2:
        return True
    if n % 2 == 0:
        return False
    for i in range(3, int(n**0.5) + 1, 2):
        if n % i == 0:
            return False
    return True

def find_prime_after_consecutive_nonprimes(n):
    count = 0
    i = 2
    while True:
        if is_prime(i):
            count = 0
        else:
            count += 1
            if count == n and is_prime(i+1):
                return i+1
        i += 1

